  // 分析单个文件内容
  const analyzeFileContent = async (file, callback) => {
    // 修正文件大小显示逻辑
    const sizeInBytes = file.fileSize || file.size;
    let sizeStr = '';
    if (sizeInBytes < 1024) {
      sizeStr = sizeInBytes + ' B';
    } else if (sizeInBytes < 1024 * 1024) {
      sizeStr = (sizeInBytes / 1024).toFixed(2) + ' KB';
    } else if (sizeInBytes < 1024 * 1024 * 1024) {
      sizeStr = (sizeInBytes / (1024 * 1024)).toFixed(2) + ' MB';
    } else {
      sizeStr = (sizeInBytes / (1024 * 1024 * 1024)).toFixed(2) + ' GB';
    }

    const fileInfo = {
      name: file.name,
      size: sizeStr,
      format: file.name.split('.').pop().toUpperCase(),
      updateTime: new Date().toLocaleString(),
    };

    // 如果是数据库中的文件，直接返回文件信息
    if (file.filePath) {
      return {
        ...fileInfo,
        rows: file.metadata?.rowCount || 0,
        columns: file.metadata?.columnCount || 0,
        missingRatio: '0%',
        previewData: []
      };
    }

    // 处理本地文件
    return new Promise((resolve) => {
      const reader = new FileReader();
      reader.onload = (e) => {
        const content = e.target.result;
        let previewData = [];

        if (fileInfo.format === 'CSV') {
          const lines = content.split('\n').filter(line => line.trim() !== '');
          if (lines.length > 0) {
            const headers = lines[0].split(',').map(h => h.trim());
            previewData = lines.slice(1, 21).map(line => {
              const values = line.split(',');
              const row = {};
              headers.forEach((header, idx) => {
                row[header] = values[idx] ? values[idx].trim() : '';
              });
              return row;
            });
          }
        } else if (fileInfo.format === 'TXT') {
          const lines = content.split(/\r?\n/);
          let currentObj = {};
          const dataArr = [];
          const keySet = new Set();

          for (let i = 0; i < lines.length && dataArr.length < 20; i++) {
            const line = lines[i];
            const kvMatch = line.match(/^([\w\s]+):\s*(.*)$/);
            if (kvMatch) {
              const key = kvMatch[1].trim();
              const value = kvMatch[2].trim();
              currentObj[key] = value;
              keySet.add(key);
            } else if (/^-{3,}$/.test(line) || line.trim() === '') {
              if (Object.keys(currentObj).length > 0) {
                dataArr.push(currentObj);
                currentObj = {};
              }
            }
          }

          if (Object.keys(currentObj).length > 0 && dataArr.length < 20) {
            dataArr.push(currentObj);
          }

          const allKeys = Array.from(keySet);
          previewData = dataArr.map(obj => {
            const row = {};
            allKeys.forEach(k => { row[k] = obj[k] || ''; });
            return row;
          });
        } else if (fileInfo.format === 'JSON') {
          try {
            let obj = JSON.parse(content);
            let arr = [];
            if (Array.isArray(obj)) {
              arr = obj;
            } else if (obj && typeof obj === 'object') {
              const arrKey = Object.keys(obj).find(k => Array.isArray(obj[k]));
              if (arrKey) {
                arr = obj[arrKey];
              } else {
                arr = [obj];
              }
            }

            const allKeys = Array.from(arr.reduce((set, item) => {
              if (item && typeof item === 'object' && !Array.isArray(item)) {
                Object.keys(item).forEach(k => set.add(k));
              }
              return set;
            }, new Set()));

            previewData = arr.slice(0, 20).map(item => {
              if (item && typeof item === 'object' && !Array.isArray(item)) {
                const row = {};
                allKeys.forEach(k => { row[k] = item[k] !== undefined ? item[k] : ''; });
                return row;
              } else {
                return { Value: item };
              }
            });
          } catch (error) {
            console.error('JSON parsing error:', error);
            previewData = [];
          }
        } else if (fileInfo.format === 'MD') {
          // 支持markdown表格解析
          const lines = content.split('\n');
          let tableStart = -1, tableEnd = -1;
          for (let i = 0; i < lines.length; i++) {
            if (lines[i].trim().startsWith('|')) {
              if (tableStart === -1) tableStart = i;
              tableEnd = i;
            } else if (tableEnd !== -1) {
              break;
            }
          }
          if (tableStart !== -1 && tableEnd > tableStart) {
            // 解析markdown表格
            const tableLines = lines.slice(tableStart, tableEnd + 1);
            const tableRows = tableLines.map(line => line.split('|').map(cell => cell.trim()).filter(cell => cell.length > 0));
            const headers = tableRows[0];
            previewData = tableRows.slice(2, 22).map(rowArr => {
              const row = {};
              headers.forEach((h, idx) => { row[h] = rowArr[idx] || ''; });
              return row;
            });
          } else {
            // 仍然保留原有的标题-内容分组逻辑
            const mdData = [];
            let currentTitle = '';
            let currentContent = '';
            for (let i = 0; i < lines.length && mdData.length < 20; i++) {
              const line = lines[i];
              if (line.startsWith('# ')) {
                if (currentTitle) {
                  mdData.push({ title: currentTitle, content: currentContent.trim() });
                }
                currentTitle = line.substring(2).trim();
                currentContent = '';
              } else {
                currentContent += line + '\n';
              }
            }
            if (currentTitle && mdData.length < 20) {
              mdData.push({ title: currentTitle, content: currentContent.trim() });
            }
            previewData = mdData;
          }
        }

        const rows = previewData.length;
        const columns = previewData.length > 0 ? Object.keys(previewData[0]).length : 0;
        let missingCount = 0;
        let totalCount = 0;

        previewData.forEach(row => {
          Object.values(row).forEach(value => {
            totalCount++;
            if (!value) missingCount++;
          });
        });

        const missingRatio = totalCount > 0 ? `${((missingCount / totalCount) * 100).toFixed(2)}%` : '0%';

        const result = {
          ...fileInfo,
          rows,
          columns,
          missingRatio,
          previewData
        };

        if (callback) {
          callback(result);
        }
        resolve(result);
      };

      reader.onerror = (error) => {
        console.error('File reading error:', error);
        const result = {
          ...fileInfo,
          rows: 0,
          columns: 0,
          missingRatio: '0%',
          previewData: []
        };
        if (callback) {
          callback(result);
        }
        resolve(result);
      };

      reader.readAsText(file);
    });
  };

