  // 辅助函数：构建文件树结构（只将有扩展名的叶子节点视为文件）
  function buildFileTree(files) {
    const root = { name: '/', children: [], isFolder: true };
    files.forEach(file => {
      // 修正：始终使用 webkitRelativePath（如果有）
      const path = file.webkitRelativePath ? file.webkitRelativePath : file.name;
      const parts = path.split('/').filter(Boolean); // 过滤空字符串，防止根目录多出空节点
      let node = root;
      for (let i = 0; i < parts.length; i++) {
        const part = parts[i];
        let child = node.children.find(c => c.name === part);
        const isLeaf = i === parts.length - 1;
        const isFile = isLeaf && /\.[^./]+$/.test(part); // 有扩展名的叶子节点才是文件
        if (!child) {
          child = {
            name: part,
            children: [],
            isFolder: !isFile,
            file: isFile ? file : undefined
          };
          node.children.push(child);
        }
        node = child;
      }
    });
    return root;
  }

  // 在 App 组件内
  const fileTree = buildFileTree(datasetFiles);

  // VSCode风格文件树组件
  function VSCodeFileTree({ node, onSelect, selectedFile, level = 0 }) {
    const [expanded, setExpanded] = useReactState(true);
    if (!node) return null;
    if (node.isFolder) {
      return (
        <div style={{ marginLeft: level * 16 }}>
          <div
            style={{ display: 'flex', alignItems: 'center', cursor: 'pointer', fontWeight: 'bold', color: '#0056d6', userSelect: 'none' }}
            onClick={() => setExpanded(e => !e)}
          >
            <span style={{ marginRight: 4 }}>{expanded ? '▼' : '▶'}</span>
            <span role="img" aria-label="folder" style={{ marginRight: 4 }}>📁</span>
            {node.name}
          </div>
          {expanded && node.children.map((child, idx) => (
            <VSCodeFileTree key={idx} node={child} onSelect={onSelect} selectedFile={selectedFile} level={level + 1} />
          ))}
        </div>
      );
    } else if (node.file) {
      const isSelected = selectedFile && selectedFile.name === node.file.name && selectedFile.webkitRelativePath === node.file.webkitRelativePath;
      return (
        <div
          style={{
            marginLeft: level * 16 + 24,
            cursor: 'pointer',
            background: isSelected ? '#e6f0ff' : 'transparent',
            color: isSelected ? '#0056d6' : '#333',
            borderRadius: 4,
            padding: '2px 4px',
            display: 'flex',
            alignItems: 'center',
            fontWeight: isSelected ? 'bold' : 'normal'
          }}
          onClick={() => onSelect(node.file)}
        >
          <span role="img" aria-label="file" style={{ marginRight: 4 }}>📄</span>
          {node.name}
        </div>
      );
    } else {
      return null;
    }
  }

