          setIsGenerating(false);

          alert('ä»£ç ç”Ÿæˆè¿‡ç¨‹ä¸­å‡ºç°é”™è¯¯: ' + data.error);
        }
      });

    } catch (error) {
      console.error('Error in handleRunClick:', error);
      alert('å¯åŠ¨å¤±è´¥: ' + error.message);
      setIsAnimating(false);
      setIsGenerating(false);
