      {/* ä¸­é—´åŒºåŸŸ */}
      <div className="center-content-panel" style={{background: '#fff', borderRadius: 12, boxShadow: '0 2px 12px rgba(0,0,0,0.06)', flex: 1, display: 'flex', flexDirection: 'column', minHeight: 0, height: '100%'}}>
