import React, { useState, useEffect } from 'react';
import './App.css';
import { uploadDataset, getDatasets, deleteDataset, generateCode, generateCodeRealtime, connectWebSocket, disconnectWebSocket, getDatasetPreview } from './services/api';

const evaluationOptions = [
  'Accuracy',
  'Efficiency',
  'Interpretability',
  'Robustness',
];

// Ê∑ªÂä†ÊµÅÁ®ãÊ≠•È™§ÁöÑÂ∏∏Èáè
const PROCESS_STEPS = {
  STEP1: {
    title: 'Step 1: ÁéØÂ¢ÉÂáÜÂ§á',
    content: [
      'ÂàùÂßãÂåñÊú∫Âô®Â≠¶‰π†ÁéØÂ¢ÉÂπ∂ÂØºÂÖ•ÂøÖË¶ÅÁöÑÂ∫ìÂíåÂ∑•ÂÖ∑',
      '1. ÂØºÂÖ•Ê†∏ÂøÉÊú∫Âô®Â≠¶‰π†Â∫ìÔºösklearn, tensorflow, pytorch',
      '2. ÂØºÂÖ•Êï∞ÊçÆÂ§ÑÁêÜÂ∑•ÂÖ∑Ôºönumpy, pandas, scipy',
      '3. ÂØºÂÖ•ÂèØËßÜÂåñÂ∑•ÂÖ∑Ôºömatplotlib, seaborn',
      '4. ËÆæÁΩÆÈöèÊú∫ÁßçÂ≠êÁ°Æ‰øùÁªìÊûúÂèØÂ§çÁé∞',
      '5. ÈÖçÁΩÆGPUÂä†ÈÄüÔºàÂ¶ÇÊûúÂèØÁî®Ôºâ'
    ]
  },
  STEP2: {
    title: 'Step 2: Êï∞ÊçÆÂ§ÑÁêÜ',
    content: [
      'ÂØπËæìÂÖ•ÁöÑÊï∞ÊçÆÈõÜËøõË°åÈ¢ÑÂ§ÑÁêÜÂíåÁâπÂæÅÂ∑•Á®ã',
      '1. Êï∞ÊçÆÊ∏ÖÊ¥óÔºöÂ§ÑÁêÜÁº∫Â§±ÂÄºÂíåÂºÇÂ∏∏ÂÄº',
      '2. ÁâπÂæÅÈÄâÊã©ÔºöÂéªÈô§Êó†ÂÖ≥ÁâπÂæÅÔºåÊèêÂèñÂÖ≥ÈîÆÁâπÂæÅ',
      '3. ÁâπÂæÅÂ∑•Á®ãÔºöÂàõÂª∫Êñ∞ÁâπÂæÅÔºåÁâπÂæÅÁªÑÂêà',
      '4. Êï∞ÊçÆÊ†áÂáÜÂåñ/ÂΩí‰∏ÄÂåñÂ§ÑÁêÜ',
      '5. Êï∞ÊçÆÈõÜÂàíÂàÜÔºöËÆ≠ÁªÉÈõÜ„ÄÅÈ™åËØÅÈõÜ„ÄÅÊµãËØïÈõÜ'
    ]
  },
  ERROR_DEBUG: {
    title: 'Error & Debug',
