  // Â§ÑÁêÜÊãñÊãΩ‰∏ä‰º†
  const handleDrop = async (e) => {
    e.preventDefault();
    setDragActive(false);

    const items = e.dataTransfer.items;
    const files = [];

    setIsUploading(true);

    const processEntry = async (entry) => {
      if (entry.isFile) {
        const file = await new Promise((resolve) => entry.file(resolve));
        files.push(file);
      } else if (entry.isDirectory) {
        const reader = entry.createReader();
        const entries = await new Promise((resolve) => {
          reader.readEntries(resolve);
        });
        for (const entry of entries) {
          await processEntry(entry);
        }
      }
    };

    const processItems = async () => {
      if (uploadType === 'folder') {
        // Êñá‰ª∂Â§πÊ®°ÂºèÔºöÈÄíÂΩíÂ§ÑÁêÜÊâÄÊúâÊñá‰ª∂
        for (let i = 0; i < items.length; i++) {
          const item = items[i];
          if (item.kind === 'file') {
            const entry = item.webkitGetAsEntry();
            if (entry) {
              await processEntry(entry);
            }
          }
        }
      } else {
        // Êñá‰ª∂Ê®°ÂºèÔºöÁõ¥Êé•Â§ÑÁêÜÊâÄÊúâÊñá‰ª∂
        const droppedFiles = Array.from(e.dataTransfer.files);
        files.push(...droppedFiles);
      }

      const allowedExts = ['CSV', 'TXT', 'JSON', 'MD'];
      const filteredFiles = files.filter(file => {
        const ext = file.name.split('.').pop().toUpperCase();
        return allowedExts.includes(ext);
      });

      if (filteredFiles.length === 0) {
        setIsUploading(false);
        return;
        }

      try {
        // ‰∏ä‰º†Âà∞ÊúçÂä°Âô®
        for (const file of filteredFiles) {
          const metadata = await analyzeFileContent(file);
          await uploadDataset(file, {
            type: 'training',
            description: `Uploaded on ${new Date().toLocaleString()}`,
            userId: 'user123',
            metadata: {
              rowCount: metadata.rows,
              columnCount: metadata.columns,
              features: metadata.previewData.length > 0 ? Object.keys(metadata.previewData[0]) : []
            }
          });
        }
        // Âè™ÊòæÁ§∫Êú¨Ê¨°‰∏ä‰º†ÁöÑÊñá‰ª∂
        setDatasetFiles(filteredFiles);
        if (filteredFiles.length > 0) {
          handleFileSelect(filteredFiles[filteredFiles.length - 1]);
        }
      } catch (error) {
        console.error('Error uploading files:', error);
        alert('File upload failed: ' + error.message);
      } finally {
        setIsUploading(false);
      }
    };

    processItems();
  };

