import React, { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';
import { uploadFile, SERVER_URL } from './services/api';
import './App.css';

function App() {
    const [files, setFiles] = useState([]);
    const [taskDescription, setTaskDescription] = useState('');
    const [isGenerating, setIsGenerating] = useState(false);
    const [realTimeSteps, setRealTimeSteps] = useState([]);
    const socketRef = useRef(null);
    const processPanelRef = useRef(null);

    useEffect(() => {
        // Scroll to the bottom of the process panel whenever new content is added
        if (processPanelRef.current) {
            processPanelRef.current.scrollTop = processPanelRef.current.scrollHeight;
        }
    }, [realTimeSteps]);


    useEffect(() => {
        // Cleanup socket connection on component unmount
        return () => {
            if (socketRef.current) {
                socketRef.current.disconnect();
                socketRef.current = null;
            }
        };
    }, []);

    const handleFileChange = async (event) => {
        const selectedFiles = Array.from(event.target.files);
        if (selectedFiles.length > 0) {
            // Clear previous files and show only newly uploaded ones
            setFiles([]);
            setRealTimeSteps([]);

            const uploadedFiles = [];
            for (const file of selectedFiles) {
                try {
                    const response = await uploadFile(file);
                    uploadedFiles.push({ name: file.name, filePath: response.filePath });
                } catch (error) {
                    console.error('Error uploading file:', file.name, error);
                    // Optionally, show an error message to the user
                }
            }
            setFiles(uploadedFiles);
        }
    };

    const connectSocketAndGenerate = () => {
        if (socketRef.current) {
            socketRef.current.disconnect();
        }

        const newSocket = io(SERVER_URL);
        socketRef.current = newSocket;

        newSocket.on('connect', () => {
            console.log('Socket connected:', newSocket.id);
            const filePaths = files.map(f => f.filePath);
            const message = {
                description: taskDescription,
                files: filePaths
            };
            newSocket.emit('start-generation', message);
        });

        newSocket.on('planning-complete', (plan) => {
            console.log('Planning complete:', plan);
            const steps = plan.map((stepTitle, index) => {
                const [summary, ...descriptionParts] = stepTitle.split(' - ');
                const description = descriptionParts.join(' - ');
                return {
                    id: index + 1,
                    summary: summary.replace(/^\d+\.\s*/, ''), // Remove leading number like "1. "
                    description: description,
                    code: '',
                    status: 'pending'
                };
            });
            setRealTimeSteps(steps);
        });

        newSocket.on('step-start', (data) => {
            console.log('Step start:', data);
            setRealTimeSteps(prevSteps => {
                const newSteps = [...prevSteps];
                const stepIndex = newSteps.findIndex(step => step.id === data.step);
                if (stepIndex !== -1) {
                    newSteps[stepIndex] = { ...newSteps[stepIndex], status: 'in-progress' };
                }
                return newSteps;
            });
        });

        newSocket.on('step-complete', (data) => {
            console.log('Step complete:', data);
             setRealTimeSteps(prevSteps => {
                const newSteps = [...prevSteps];
                const stepIndex = newSteps.findIndex(step => step.id === data.step);
                if (stepIndex !== -1) {
                    newSteps[stepIndex] = { 
                        ...newSteps[stepIndex], 
                        status: 'complete', 
                        code: data.code 
                    };
                }
                return newSteps;
            });
        });

        newSocket.on('generation-complete', () => {
            console.log('Generation complete');
            setIsGenerating(false);
            newSocket.disconnect();
            socketRef.current = null;
        });

        newSocket.on('error', (error) => {
            console.error('An error occurred:', error);
            setIsGenerating(false);
            // Find the in-progress step and mark it as error
            setRealTimeSteps(prevSteps => prevSteps.map(step => 
                step.status === 'in-progress' ? { ...step, status: 'error', code: error.message || 'An unknown error occurred.' } : step
            ));
            newSocket.disconnect();
            socketRef.current = null;
        });
    };

    const handleRunClick = () => {
        if (!taskDescription.trim()) {
            alert('Please enter a task description.');
            return;
        }
        setIsGenerating(true);
        setRealTimeSteps([]);
        connectSocketAndGenerate();
    };

    const renderStepStatusIcon = (status) => {
        switch (status) {
            case 'pending':
                return <div className="status-icon pending"></div>;
            case 'in-progress':
                return <div className="status-icon in-progress"></div>;
            case 'complete':
                return <div className="status-icon complete">âœ“</div>;
            case 'error':
                return <div className="status-icon error">!</div>;
            default:
                return null;
        }
    };

    return (
        <div className="App">
            <div className="main-container">
                <div className="left-panel">
                    <h1>ML Code Generator</h1>

                    <div className="upload-section">
                        <h2>1. Upload Your Datasets</h2>
                        <label htmlFor="file-upload" className="custom-file-upload">
                           Click to Upload
                        </label>
                        <input id="file-upload" type="file" multiple onChange={handleFileChange} />
                        <div className="file-list">
                            {files.map((file, index) => (
                                <p key={index} className="file-item">{file.name}</p>
                            ))}
                        </div>
                    </div>

                    <div className="task-section">
                        <h2>2. Describe Your Task</h2>
                        <textarea
                            value={taskDescription}
                            onChange={(e) => setTaskDescription(e.target.value)}
                            placeholder="e.g., 'Build a classification model to predict iris species.'"
                        />
                    </div>

                    <button
                        className="run-button"
                        onClick={handleRunClick}
                        disabled={isGenerating || files.length === 0}
                    >
                        {isGenerating ? 'Generating...' : 'Run'}
                    </button>
                </div>

                <div className="right-panel" ref={processPanelRef}>
                    <h2>Process Details</h2>
                    {isGenerating && realTimeSteps.length === 0 && <p className="loading-text">Planning the steps...</p>}
                    <div className="timeline-container">
                        {realTimeSteps.map((step, index) => (
                             <div key={step.id} className={`timeline-item ${step.status}`}>
                                {renderStepStatusIcon(step.status)}
                                <div className="timeline-content">
                                    <h3>
                                        <span>Step {step.id}: </span>
                                        <strong>{step.summary}</strong>
                                    </h3>
                                    {step.description && <p className="step-description">{step.description}</p>}
                                    {step.code && step.status !== 'error' && (
                                         <div className="code-block">
                                            <h4>Generated Code:</h4>
                                            <pre><code>{step.code}</code></pre>
                                        </div>
                                    )}
                                     {step.status === 'error' && (
                                        <div className="error-message">
                                            <h4>Error:</h4>
                                            <pre><code>{step.code}</code></pre>
                                        </div>
                                    )}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
