              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );

  // 辅助函数：获取状态颜色
  const getStatusColor = (status) => {
    switch (status) {
      case 'planning':
      case 'generating':
      case 'checking':
        return '#ffc107'; // 黄色 - 进行中
      case 'complete':
      case 'checked':
      case 'revised':
        return '#28a745'; // 绿色 - 完成
      case 'error':
        return '#dc3545'; // 红色 - 错误
      case 'refining':
        return '#17a2b8'; // 蓝色 - 优化中
      default:
        return '#6c757d'; // 灰色 - 默认
    }
  };

  // 辅助函数：获取状态文本
  const getStatusText = (status) => {
    switch (status) {
      case 'planning':
        return 'Planning';
      case 'generating':
        return 'Generating';
      case 'checking':
        return 'Checking';
      case 'complete':
        return 'Complete';
      case 'checked':
        return 'Checked';
      case 'revised':
        return 'Revised';
      case 'error':
        return 'Error';
      case 'refining':
        return 'Refining';
      default:
        return 'Idle';
    }
  };

  // 辅助函数：获取步骤边框颜色
  const getStepBorderColor = (status) => {
    switch (status) {
      case 'generating':
      case 'checking':
        return '#007bff'; // Blue for in-progress
      case 'complete':
      case 'checked':
      case 'revised':
        return '#28a745'; // Green for success
      case 'error':
        return '#dc3545'; // Red for error
      case 'pending':
        return '#6c757d'; // Grey for pending
      default:
        return '#e9ecef'; // Default grey
    }
  };

  // 辅助函数：获取步骤状态文本
  const getStepStatusText = (status) => {
    switch (status) {
      case 'generating':
        return 'Generating';
      case 'checking':
        return 'Checking';
      case 'complete':
        return 'Complete';
      case 'checked':
        return 'Checked';
      case 'revised':
        return 'Revised';
      case 'error':
        return 'Error';
      default:
        return 'Pending';
    }
  };

  // 修改 handleOutcomeClick 函数
  const handleOutcomeClick = () => {
    setShowProcessPanel(false);
    setShowOutcomeButton(false);
    // 切换到代码标签页
    setTab('code');
