        </div>
        <button className="run-btn" onClick={handleRunClick} style={{ height: 44, fontSize: 20, marginTop: 24, width: '100%' }} disabled={isUploading || isGenerating}>
          {isGenerating ? 'GENERATING...' : 'RUN'}
