                    <div className="data-preview">
                      <h4>Data preview</h4>
                      <table>
                        <thead>
                          <tr>
                            {filePreview.previewData && filePreview.previewData.length > 0 && (() => {
                              const first = filePreview.previewData[0];
                              if (first && typeof first === 'object' && !Array.isArray(first)) {
                                return Object.keys(first).map((key) => (
                                  <th key={key}>{key}</th>
                                ));
                              } else {
                                return <th>Value</th>;
